mapper.insert:
insert into lineup(
    user_key
	,team_key
	,game_key
	,code_key
	,team_side
	,lineup_index
	,mercenary_state
)
values

(


			NULL


			#{item.user_key}


			,#{item.team_key}
			,#{item.game_key}
			,#{item.code_key}
			,#{item.team_side}
			,#{item.lineup_index}
			,#{item.mercenary_state}
)

mapper.delete:
delete 
from lineup 
where game_key=#{game_key} AND team_side=#{team_side}
