mapper.select:
select
count(*) from User
where user_id=#{ user_id }

mapper.insert:
INSERT INTO User(
user_id
, user_password
, user_name
, user_mail
, user_phone
, user_address1
, user_address2
, user_position
, user_intro
, user_delete
) VALUES(
#{
user_id }
, #{ user_password }
, #{ user_name }
, #{ user_mail }
, #{ user_phone }
, #{ user_address1 }
, #{ user_address2 }
, #{ user_position }
, #{ user_intro }
, 1
)

mapper.update:
update User set
user_name = #{ user_name }
, user_mail = #{ user_mail }
, user_phone = #{ user_phone }
, user_address1 = #{ user_address1 }
, user_address2 = #{ user_address2 }
, user_position = #{ user_position }
, user_intro = #{ user_intro }
where user_id=#{ user_id }
